CREATE TABLE COURSES (COURSEID INT NOT NULL UNSIGNED AUTO_INCREMENT, CODE VARCHAR2(8) NOT NULL, NAME VARCHAR2(100) NOT NULL, SEMESTER VARCHAR2(1) NOT NULL, PRIMARY KEY(COURSEID), UNIQUE KEY course (CODE, SEMESTER));
CREATE TABLE TEXTBOOKS (TEXTBOOKID INT NOT NULL UNSIGNED AUTO_INCREMENT, ISBN BIGINT NOT NULL UNSIGNED, VARCHAR2(300) PHOTO, VARCHAR2(300) NAME NOT NULL, VARCHAR2(1000) DESCRIPTION, VARCHAR2(200) AUTHOR, VARCHAR2(40) EDITION, PRIMARY KEY(ISBN));
CREATE TABLE USERS (USERID INT NOT NULL UNSIGNED AUTO_INCREMENT, USERNAME VARCHAR2(50) NOT NULL, EMAIL VARCHAR2(200) NOT NULL, NAME VARCHAR2(200) NOT NULL, PRIMARY KEY(USERID), UNIQUE KEY usern (USERNAME));
CREATE TABLE COURSE_TEXTBOOK_LINK (COURSE_TEXTBOOK_LINK_ID INT NOT NULL UNSIGNED AUTO_INCREMENT, COURSEID INT NOT NULL UNSIGNED, TEXTBOOKID NOT NULL UNSIGNED, PRIMARY KEY(COURSE_TEXTBOOK_LINK_ID));
CREATE TABLE LISTINGS (LISTINGID INT NOT NULL UNSIGNED AUTO_INCREMENT, TEXTBOOKID INT NOT NULL UNSIGNED, USERID INT NOT NULL UNSIGNED, PRICE INT NOT NULL UNSIGNED, PRIMARY KEY(LISTINGID));
